import { KLineChartData, TimeSharingType } from '../components/k-line/k-line';
interface SMAType {
    time: number;
    value: number;
}
/**
 * 计算 SMA
 */
declare const calculateSMA: (data: any, count: number, price: string) => Array<SMAType>;
/**
 * 计算EMA
 * // ema 上一日
 * // price 价格,dif
 * // period 日期
 */
declare const calculateEMA: (ema: number, price: number, period: number) => number;
interface CalMacdType {
    emaFast: number;
    emaSlow: number;
    dif: number;
    dea: number;
    value: number;
    time: number;
}
/**
 * 计算 MACD
 * fast 快线
 * slow 慢线
 * signal 日期
 */
declare const calculateMACD: (data: any, fast: number, slow: number, signal: number) => Array<CalMacdType>;
declare const updateTimeOfData: (value: number, type: TimeSharingType, kLineChartData: Array<KLineChartData>) => Array<KLineChartData>;
export { calculateSMA, calculateMACD, calculateEMA, updateTimeOfData };
